parameters.jsCode:
// Traiter les données reçues dans le JSON
return $input.all().map(item => {
  const fields = item.json.fields;

  // Structure pour Qdrant
  return {
    id: fields["ID"], // Utilise l'ID unique de l'article
    payload: {
      title: fields["Titre"] || "", // Titre de l'article
      content: fields["Contenu Article"] || "", // Contenu complet
      url: fields["URL Article"] || "", // URL vers l'article
      category: fields["Catégorie"] || "", // Catégorie (ID ou nom)
      date_modified: fields["Dernière Modification"] || "", // Dernière modification
      created_time: item.json.createdTime || "" // Date de création dans Airtable
    }
  };
});

